() => {
  const [key, setKey] = React.useState(0);
  const [count, setCount] = React.useState(0);
  return (
    <Stack>
      <Dropzone key={key} onFilesChange={(fs) => setCount(fs.length)} />
      <Stack direction='row' gap={1}>
        <Typography variant='subtitle'>Files: {count}</Typography>
        <Button variant='outlined' onClick={() => { setKey((k) => k + 1); setCount(0); }}>Clear files</Button>
      </Stack>
    </Stack>
  );
}
