() => {
  const [page, setPage] = React.useState(5);
  const [follow, setFollow] = React.useState(true);
  return (<Stack>
    <Pagination count={50} visibleWindow={7} autoFollowActive={follow} page={page} onChange={setPage} />
    <Stack direction='row' sx={{ alignItems: 'center' }}>
      <Typography variant='subtitle'>autoFollowActive</Typography>
      <Switch checked={follow} onChange={setFollow} aria-label='toggle autoFollowActive' />
    </Stack>
  </Stack>);
}
