() => {
  const useContact = Valet.createFormStore({ name: '', email: '' });
  return (
    <FormControl useStore={useContact} onSubmitValues={(values) => console.log(values)}>
      <Stack>
        <TextField name='name' label='Name' />
        <TextField name='email' type='email' label='Email' autoComplete='email' />
        <Button type='submit'>Submit</Button>
      </Stack>
    </FormControl>
  );
}

() => {
  const useProfile = Valet.createFormStore({ displayName: '' });
  return (
    <FormControl
      useStore={useProfile}
      onSubmitValues={(values, event) => {
        const fd = new FormData(event.currentTarget);
        // Merge: prefer typed values; attach file for upload
        const payload = { ...values, avatar: fd.get('avatar') };
        console.log(payload);
      }}
    >
      <Stack>
        <TextField name='displayName' label='Display name' />
        <input type='file' name='avatar' />
        <Button type='submit'>Save</Button>
      </Stack>
    </FormControl>
  );
}
