() => {
  const { theme } = Valet.useTheme();
  return <AppBar color={theme.colors.primary} textColor={theme.colors.primaryText} left={<Typography>Primary</Typography>} />;
}

() => {
  const [active, setActive] = React.useState('home');
  const navigation = [
    { id: 'home', label: 'Home', active: active === 'home', onClick: () => setActive('home') },
    { id: 'docs', label: 'Docs', active: active === 'docs', onClick: () => setActive('docs') },
    { id: 'account', label: 'Account', active: active === 'account', onClick: () => setActive('account') }
  ];
  return (
    <AppBar
      fixed={false}
      portal={false}
      left={<Typography variant='subtitle'>Product</Typography>}
      navigation={navigation}
      navigationLabel='Primary navigation'
      right={<Button size='sm' variant='outlined'>CTA</Button>}
    />
  );
}

() => {
  const navigation = [
    { id: 'home', icon: <Icon icon='mdi:home' />, ariaLabel: 'Home', active: true },
    { id: 'search', icon: <Icon icon='mdi:magnify' />, ariaLabel: 'Search', href: '#' },
    { id: 'profile', icon: <Icon icon='mdi:account' />, ariaLabel: 'Profile', href: '#' }
  ];
  return (
    <AppBar
      fixed={false}
      portal={false}
      navigation={navigation.map((item) => ({ ...item, iconOnly: true }))}
      navigationLabel='Icon navigation'
      right={<Button size='sm'>Sign up</Button>}
    />
  );
}
