() => {
  const [items, setItems] = React.useState([{name:'Ada',role:'Programmer'},{name:'Alan',role:'Mathematician'}]);
  const [sel, setSel] = React.useState(null);
  return (<Stack>
    <List data={items} getTitle={(i)=>i.name} getSubtitle={(i)=>i.role} selectable reorderable selected={sel} onSelectionChange={setSel} onReorder={setItems} />
    <Typography variant='subtitle'>Selected: {sel?.name || '—'}</Typography>
  </Stack>);
}
