(() => {
  const DateForm = ({ onSubmit }) => (
    <Stack direction='row' gap={1}>
      <DateSelector onChange={(v)=>void 0} value='' />
      <IconButton icon='carbon:checkmark' aria-label='Confirm' onClick={()=>onSubmit('2025-01-01')} />
    </Stack>
  );
  const [messages, setMessages] = React.useState([
    { role: 'assistant', content: 'Pick a date', form: ({ onSubmit }) => <DateForm onSubmit={onSubmit} /> }
  ]);
  return <RichChat messages={messages} onFormSubmit={(val)=>console.log(val)} />;
})()
