(() => {
  const columns = [
    { header: 'ID', accessor: 'id', align: 'right' },
    { header: 'Name', accessor: 'name' },
    { header: 'City', accessor: 'city' },
  ];
  const data = [
    { id: 1, name: 'Ada Lovelace', city: 'London' },
    { id: 2, name: 'Alan Turing', city: 'Manchester' },
    { id: 3, name: 'Grace Hopper', city: 'New York' },
  ];
  return (<Panel fullWidth><Table data={data} columns={columns} striped hoverable constrainHeight /></Panel>);
})()

(() => {
  const columns = [
    { header: 'ID', accessor: 'id', align: 'right', sortable: true },
    { header: 'Name', accessor: 'name', sortable: true },
    { header: 'Age', accessor: 'age', align: 'right', sortable: true },
  ];
  const data = [
    { id: 1, name: 'Katherine Johnson', age: 41 },
    { id: 2, name: 'Tim Berners‑Lee', age: 35 },
    { id: 3, name: 'Linus Torvalds', age: 29 },
  ];
  const Demo = () => {
    const [selected, setSelected] = React.useState([]);
    return (<Stack>
      <Panel fullWidth>
        <Table data={data} columns={columns} selectable='single' initialSort={{ index: 0 }} onSelectionChange={setSelected} />
      </Panel>
      <Typography variant='subtitle'>Selected: {selected.map((r) => r.id).join(', ') || '—'}</Typography>
    </Stack>);
  };
  return <Demo />;
})()

(() => {
  type Row = { id: number; name: string; city: string };
  const columns = [
    { header: 'ID', accessor: 'id', align: 'right' },
    { header: 'Name', accessor: 'name' },
    { header: 'City', accessor: 'city' },
  ];
  const data = Array.from({ length: 60 }, (_, i) => ({ id: i + 1, name: 'User ' + (i + 1), city: 'City ' + (i % 8) }));
  return (
    <Panel fullWidth>
      <Table
        data={data}
        columns={columns}
        striped
        hoverable
        constrainHeight
        minConstrainedRows={4}
        maxExpandedRows={20}
      />
    </Panel>
  );
})()

(() => {
  type Row = { id: number; name: string; city: string };
  const columns = [
    { header: 'ID', accessor: 'id', align: 'right' },
    { header: 'Name', accessor: 'name' },
    { header: 'City', accessor: 'city' },
  ];
  const data = Array.from({ length: 48 }, (_, i) => ({ id: i + 1, name: 'Row ' + (i + 1), city: 'Metro ' + (i % 5) }));
  const Demo = () => {
    const [page, setPage] = React.useState(1);
    return (
      <Panel fullWidth>
        <Table
          data={data}
          columns={columns}
          striped
          paginate
          maxExpandedRows={12}
          page={page}
          onPageChange={setPage}
          paginationWindow={7}
        />
      </Panel>
    );
  };
  return <Demo />;
})()
