(() => {
  const data = [
    { id: 'fruit', data: { label: 'Fruit' }, children: [
      { id: 'apple', data: { label: 'Apple' } },
      { id: 'banana', data: { label: 'Banana' } }
    ]}
  ];
  return (<Tree nodes={data} getLabel={(n) => n.label} defaultExpanded={['fruit']} variant='chevron' />);
})()

