    { id: 'src', data: { label: 'src' }, children: [
      { id: 'index.ts', data: { label: 'index.ts' } }
    ]}
  ];
  return (<Tree nodes={files} getLabel={(n) => n.label} defaultExpanded={['src']} variant='files' />);
})()
